const AppReducer = (state, action) => {

switch (action.type){
    
    case  'Add_Transaction':
        state.transactions.push(action.payload) 
        return state;
        // transactions: [
            // ...state.transactions,
            // action.payload
            // ]
    case  'Delete_Transaction':
        return state;
    default: 
        return state;
}

};
export default AppReducer;
